sys_script_client.script:
function onSubmit() {
	//Type appropriate comment here, and begin script below
	var startdate = g_form.getValue('on_date').split(' ')[0];
	var enddate = g_form.getValue('end_date').split(' ')[0];

	// start date
	var tempStartDate = startdate.split('/');
	var temp = tempStartDate[1];
	tempStartDate[1] = tempStartDate[0];
	tempStartDate[0] = temp;
	startdate = new Date(tempStartDate.join('/'));

	// end date
	var tempEndDate = enddate.split('/');
	var tempEnd = tempEndDate[1];
	tempEndDate[1] = tempEndDate[0];
	tempEndDate[0] = tempEnd;
	enddate = new Date(tempEndDate.join('/'));



	if(enddate < startdate){
		g_form.addErrorMessage("Prior date");
		return false;

	}
  var timeDiff = Math.abs(startdate - enddate);
	var diffDays = Math.ceil(timeDiff / (1000 * 3600 * 24));
	alert(diffDays);
}
